INSERT INTO `hotels` (`id`, `h_name`, `h_image`, `h_address`, `h_phone`, `h_anbum_image`, `h_price`, `h_price_contact`, `h_sale`, `h_description`, `h_content`, `h_status`, `h_service_id`, `h_location_id`, `h_user_id`, `created_at`, `updated_at`) VALUES
(1, 'VINPEARL DISCOVERY 3 PHU QUOC', 'https://www.maybourne.com/siteassets/exquisite-hotels/exquisite-hotels-mbh-pool-1920-720.jpg', NULL, '0343754517', NULL, 76856785, 0, 0, 'Tọa lạc trên một ngọn núi và bao quanh bởi biển, Romana Resort & Spa Phan Thiết mang đến cho khách hàng khung cảnh tuyệt đẹp của vịnh và núi. Khu nghỉ mát có 4 phòng Junior Suites, 28 biệt thự có bể bơi riêng và 64 phòng Deluxe. Tất cả các phòng đều nhìn ra đại dương và được thiết kế riêng tư sang trọng. Các Nhà hàng và Bar tại Romana Resort & Spa là một trong những nhà hàng và bar tốt nhất ở Mũi Né, Phan Thiết. Nhà hàng Panorama sẽ mang đến cho quý khách sự trải nghiệm những khoảnh khắc đáng nhớ khi vừa thưởng thức bữa tối tuyệt vời vừa ngắm nhìn cảnh hoàng hôn tuyệt đẹp. Nhà hàng phục vụ các thực đơn đa dạng, từ các món ăn khai vị tinh tế, salad nhiệt đới đến các loại hải sản tươi sống.', NULL, 1, 3, 3, NULL, '2021-07-10 03:58:06', '2023-05-03 13:48:56'),
(2, 'Vinpearl Resort & Spa Đà Nẵng', 'https://dulichviet.com.vn/images/bandidau/NOI-DIA/Da-Nang/Radisson-da-nang-du-lich-viet-20.jpg', '23 Trường Sa, Hoà Hải, Ngũ Hành Sơn, Đà Nẵng 550000', '0343754517', NULL, 8550000, 0, 0, 'Tọa lạc trên một ngọn núi và bao quanh bởi biển, Romana Resort & Spa Phan Thiết mang đến cho khách hàng khung cảnh tuyệt đẹp của vịnh và núi. Khu nghỉ mát có 4 phòng Junior Suites, 28 biệt thự có bể bơi riêng và 64 phòng Deluxe. Tất cả các phòng đều nhìn ra đại dương và được thiết kế riêng tư sang trọng. Các Nhà hàng và Bar tại Romana Resort & Spa là một trong những nhà hàng và bar tốt nhất ở Mũi Né, Phan Thiết. Nhà hàng Panorama sẽ mang đến cho quý khách sự trải nghiệm những khoảnh khắc đáng nhớ khi vừa thưởng thức bữa tối tuyệt vời vừa ngắm nhìn cảnh hoàng hôn tuyệt đẹp. Nhà hàng phục vụ các thực đơn đa dạng, từ các món ăn khai vị tinh tế, salad nhiệt đới đến các loại hải sản tươi sống.', '<p>VỊ TR&Iacute;</p>\n\n<p>Nằm tr&ecirc;n đường Trường Sa, Ngũ H&agrave;nh Sơn &ndash; nợi được mệnh danh l&agrave; con đường 5 sao về du lịch của Th&agrave;nh phố Đ&agrave; Nẵng - trung t&acirc;m của con đường di sản miền Trung v&agrave; l&agrave; th&agrave;nh phố đ&aacute;ng sống nhất Việt Nam. Vinpearl Resort &amp; Spa Đ&agrave; Nẵng với kiến tr&uacute;c t&acirc;n cổ điển đặc trưng, nằm b&ecirc;n b&atilde;i biển Non Nước xinh đẹp, với hệ thống 122 biệt thự nghỉ dưỡng, kh&ocirc;ng chỉ gi&uacute;p bạn trải nghiệm một m&ocirc;i trường sống xứng tầm giữa thi&ecirc;n nhi&ecirc;n tuyệt đẹp v&agrave; hệ thống tiện nghi &nbsp;hiện đại. Ch&iacute;nh v&igrave; vậy, c&oacute; thể n&oacute;i Vinpearl Resort &amp; Spa Đ&agrave; Nẵng l&agrave; địa điểm th&iacute;ch hợp nhất khi chọn loại h&igrave;nh du lịch nghỉ dưỡng ở Đ&agrave; nẵng.</p>\n\n<p>Vị tr&iacute; thuận lợi, du kh&aacute;ch c&oacute; thể dễ d&agrave;ng di chuyển đến nhiều danh thắng nổi tiếng của Đ&agrave; Nẵng như Cầu Rồng (12km), chợ H&agrave;n (12km), ch&ugrave;a Linh Ứng (16,3km), Hội An (17km),</p>\n\n<p>+ Khu du lịch B&agrave; N&agrave; Hills (33km).</p>\n\n<p>Gi&aacute; v&eacute; 700.000vnđ/ người lớn, 550.000vnđ/ trẻ em. Địa điểm mua v&eacute;: tại quầy dưới ch&acirc;n n&uacute;i Khu du lịch B&agrave; N&agrave; Hills. Mua v&eacute; online tại trang web của Sun World B&agrave; N&agrave; Hills. Văn ph&ograve;ng đại diện tại số 93 Nguyễn Văn Linh, Q.Hải Ch&acirc;u. B&agrave; N&agrave; Hills quần thể du lịch nghỉ dưỡng kết hợp vui chơi giải tr&iacute; đẳng cấp tại Việt Nam. Đến đ&acirc;y, du kh&aacute;ch được trải nghiệm một trong 10 tuyến c&aacute;p treo ấn tượng nhất thế giới (do CNN b&igrave;nh chọn).</p>\n\n<p>+ C&ocirc;ng vi&ecirc;n suối kho&aacute;ng n&oacute;ng n&uacute;i Thần T&agrave;i (36km)</p>\n\n<p>Gi&aacute; v&eacute;: V&eacute; v&agrave;o cổng: 300.000vnđ/ người lớn, 150.000vnđ/ trẻ em từ 1-1m3, miễn ph&iacute; trẻ dưới 1m.</p>\n\n<p>Gi&aacute; v&eacute; bao gồm c&aacute;c dịch vụ: Tắm kho&aacute;ng tại Huyệt Long Hồ v&agrave; c&aacute;c hồ tự nhi&ecirc;n kh&aacute;c. Thư gi&atilde;n với với x&ocirc;ng hơi kh&ocirc;, x&ocirc;ng hơi ướt. Trượt phao chinh phục d&ograve;ng s&ocirc;ng Lười. Tham quan c&aacute;c mỏ kho&aacute;ng ph&aacute;t lộ. Ng&acirc;m ch&acirc;n trực tiếp tại một trong những điểm ph&aacute;t lộ tại mỏ kho&aacute;ng. Thỏa sức tham gia c&aacute;c tr&ograve; chơi nước v&agrave; massage tại hồ sục Jacuzzii. Vui chơi c&ocirc;ng vi&ecirc;n nước.</p>\n\n<p>**Lưu &yacute;: Từ C&ocirc;ng vi&ecirc;n sang Khu du lịch B&agrave; N&agrave; Hills khoảng 17km, xung quanh kh&aacute; vắng n&ecirc;n du kh&aacute;ch n&ecirc;n ưu ti&ecirc;n lựa chọn ăn uống ngay trong khu&ocirc;n vi&ecirc;n.</p>\n\n<p>DI CHUYỂN</p>\n\n<p>Di chuyển bằng đường h&agrave;ng kh&ocirc;ng: Từ H&agrave; Nội, TP.HCM,&hellip; c&aacute;c bạn c&oacute; thể di chuyển bằng m&aacute;y bay xuống s&acirc;n bay quốc tế Đ&agrave; Nẵng.<br />\n+ TP.HCM &ndash; s&acirc;n bay quốc tế Đ&agrave; Nẵng: gi&aacute; dao động từ 647.000 &ndash; 2.469.000 vnđ/người/chuyến (t&ugrave;y v&agrave;o thời gian của chuyến bay, số ghế,&hellip;). &nbsp;Thời gian di chuyển khoảng 1 tiếng 20 ph&uacute;t.</p>\n\n<p>+ H&agrave; Nội &ndash; s&acirc;n bay quốc tế Đ&agrave; Nẵng: gi&aacute; dao động từ 647.000 &ndash; 2.369.000 vnđ/người/chuyến (t&ugrave;y v&agrave;o thời gian của chuyến bay, số ghế,&hellip;). &nbsp;Thời gian di chuyển khoảng 1 tiếng 20 ph&uacute;t.</p>\n\n<p>Từ s&acirc;n bay Đ&agrave; Nẵng thuận tiện nhất l&agrave; sử dụng taxi của c&aacute;c h&atilde;ng xe phổ biến tại Đ&agrave; Nẵng - Hội An như Mai Linh, Vinasun, Ti&ecirc;n Sa,.... để đến Vinpearl Resort &amp; Spa Đ&agrave; Nẵng.</p>\n\n<p>Từ s&acirc;n bay Đ&agrave; Nẵng về resort khoảng 12,7km tương đương 24 ph&uacute;t đi xe. Gi&aacute; taxi một chiều khoảng 182.000 - 211.000 vnđ t&ugrave;y loại xe, t&ugrave;y h&atilde;ng xe bạn chọn v&agrave; thời điểm đặt xe.</p>\n\n<p>Di chuyển bằng xe kh&aacute;ch: Từ H&agrave; Nội, hoặc TPHCM,&hellip; c&aacute;c bạn c&oacute; thể di chuyển bằng xe kh&aacute;ch đến Đ&agrave; Nẵng với gi&aacute; khoảng 320.000vnđ/người/chuyến. Sau đ&oacute; di chuyển bằng taxi đến Vinpearl Resort &amp; Golf Nam Hội An.<br />\nTIỆN &Iacute;CH V&Agrave; GIẢI TR&Iacute;</p>\n\n<p>Nh&agrave; h&agrave;ng v&agrave; bar: Trong một kh&ocirc;ng gian được thiết kế sang trọng, độc đ&aacute;o, tinh tế v&agrave; tỉ mĩ đến từng chi tiết, bạn sẽ được thưởng thức những tinh hoa tuyệt vời, đặc sắc v&agrave; đa dạng trong ẩm thực &Aacute;-&Acirc;u.<br />\nGiải tr&iacute;: s&acirc;n tennis rộng lớn đặt trong khu vực sảnh, s&acirc;n tennis của Vinpearl cung cấp dịch vụ cho thu&ecirc; thiết bị v&agrave; người chơi, trọng t&agrave;i cũng như huấn luyện vi&ecirc;n chuy&ecirc;n nghiệp, đ&aacute;p ứng mọi y&ecirc;u cầu của du kh&aacute;ch. Đặc biệt, Vinpearl Resort &amp; Spa Đ&agrave; Nẵng sở hữu một b&atilde;i biển ri&ecirc;ng tại b&atilde;i Non Nước y&ecirc;n b&igrave;nh v&agrave; hoang sơ, gi&uacute;p qu&yacute; kh&aacute;ch c&oacute; kh&ocirc;ng gian ri&ecirc;ng, thoải m&aacute;i đắm m&igrave;nh trong biển xanh, c&aacute;t trắng, nắng v&agrave;ng, tận hưởng những l&agrave;n gi&oacute; tươi mới từ biển cả v&agrave; tham gia c&aacute;c m&ocirc;n thể thao tr&ecirc;n biển. To&agrave;n bộ hệ thống của Vinpearl phục vụ đầy đủ c&aacute;c spa, khu tập thể h&igrave;nh, c&acirc;u lạc bộ cho trẻ em,&hellip; với mong muốn l&agrave;m h&agrave;i long, gi&uacute;p bạn c&oacute; một kỳ nghỉ thư giản tuyệt đối tại c&aacute;c hệ th&ocirc;ng của Vinpear n&oacute;i chung v&agrave; Vinpearl Resort &amp; Spa Đ&agrave; Nẵng n&oacute;i ri&ecirc;ng.</p>', 1, 3, 3, NULL, '2021-07-15 17:08:27', '2022-08-04 18:59:49'),
(3, 'VINPEARL RESORT & GOLF NAM HỘI AN', 'https://dulichviet.com.vn/images/bandidau/NOI-DIA/Phu-Quoc/Seashells-Resort-phu-quoc-du-lich-viet-17.jpg', NULL, '0343754517', NULL, 4150000, 0, 0, 'Tọa lạc trên một ngọn núi và bao quanh bởi biển, Romana Resort & Spa Phan Thiết mang đến cho khách hàng khung cảnh tuyệt đẹp của vịnh và núi. Khu nghỉ mát có 4 phòng Junior Suites, 28 biệt thự có bể bơi riêng và 64 phòng Deluxe. Tất cả các phòng đều nhìn ra đại dương và được thiết kế riêng tư sang trọng. Các Nhà hàng và Bar tại Romana Resort & Spa là một trong những nhà hàng và bar tốt nhất ở Mũi Né, Phan Thiết. Nhà hàng Panorama sẽ mang đến cho quý khách sự trải nghiệm những khoảnh khắc đáng nhớ khi vừa thưởng thức bữa tối tuyệt vời vừa ngắm nhìn cảnh hoàng hôn tuyệt đẹp. Nhà hàng phục vụ các thực đơn đa dạng, từ các món ăn khai vị tinh tế, salad nhiệt đới đến các loại hải sản tươi sống.', '<p>VỊ TR&Iacute;</p>\r\n\r\n<p>Nằm ở ph&iacute;a Nam của th&agrave;nh phố Hội An, tọa lạc b&ecirc;n bờ biển B&igrave;nh Minh nguy&ecirc;n sơ d&agrave;i 1.300m. Vinpearl Resort &amp; Golf Nam Hội An l&agrave; khu du lịch nghỉ dưỡng phức hợp đẳng cấp đầu ti&ecirc;n hội tụ ho&agrave;n hảo những tinh hoa của hệ sinh th&aacute;i trong &ldquo;ốc đảo&rdquo; thi&ecirc;n nhi&ecirc;n nhiệt đới.</p>\r\n\r\n<p>Được thiết kế theo phong c&aacute;ch kiến tr&uacute;c nhiệt đới đương đại, mới lạ, lấy cảm hứng từ những miền thi&ecirc;n nhi&ecirc;n nhiệt đới với m&agrave;u xanh bất tận của biển v&agrave; trời, 132 căn biệt thự tại Vinpearl Resort &amp; Golf Nam Hội An được thiết kế tối giản, gần gũi với m&ocirc;i trường, mang đến một phong c&aacute;ch nghỉ dưỡng hiện đại, thư th&aacute;i h&ograve;a m&igrave;nh c&ugrave;ng đại dương. Mỗi căn biệt thự l&agrave; một tuyệt t&aacute;c kiến tr&uacute;c tinh tế với bể bơi v&agrave; khoảng s&acirc;n vườn xanh m&aacute;t bao quanh, mở ra khoảng trời y&ecirc;n b&igrave;nh, ri&ecirc;ng tư tuyệt đối b&ecirc;n bờ biển thi&ecirc;n đường.</p>\r\n\r\n<p>DI CHUYỂN</p>\r\n\r\n<p>Di chuyển bằng đường h&agrave;ng kh&ocirc;ng: Từ H&agrave; Nội, TP.HCM,&hellip; c&aacute;c bạn c&oacute; thể di chuyển bằng m&aacute;y bay xuống s&acirc;n bay quốc tế Đ&agrave; Nẵng hoặc s&acirc;n bay Chu Lai Quảng Nam.<br />\r\n+ TP.HCM &ndash; s&acirc;n bay quốc tế Đ&agrave; Nẵng: gi&aacute; dao động từ 647.000 &ndash; 2.469.000 vnđ/người/chuyến (t&ugrave;y v&agrave;o thời gian của chuyến bay, số ghế,&hellip;). &nbsp;Thời gian di chuyển khoảng 1 tiếng 20 ph&uacute;t.</p>\r\n\r\n<p>+TP.HCM &ndash; s&acirc;n bay Chu Lai, Quảng Nam: gi&aacute; dao động từ 1.049.000 &ndash; 2.469.000 vnđ/người/chuyến. Thời gian di chuyển 1 tiếng 20 ph&uacute;t.</p>\r\n\r\n<p>+ H&agrave; Nội &ndash; s&acirc;n bay quốc tế Đ&agrave; Nẵng: gi&aacute; dao động từ 647.000 &ndash; 2.369.000 vnđ/người/chuyến (t&ugrave;y v&agrave;o thời gian của chuyến bay, số ghế,&hellip;). &nbsp;Thời gian di chuyển khoảng 1 tiếng 20 ph&uacute;t.</p>\r\n\r\n<p>+ H&agrave; Nội &ndash; s&acirc;n bay Chu Lai, Quảng Nam: gi&aacute; dao động từ 525.000 &ndash; 939.000 vnđ/người/chuyến. Thời gian di chuyển 1 tiếng 30 ph&uacute;t.</p>\r\n\r\n<p>Từ s&acirc;n bay Đ&agrave; Nẵng hoặc Chu Lai &nbsp;thuận tiện nhất l&agrave; sử dụng taxi của c&aacute;c h&atilde;ng xe phổ biến tại Đ&agrave; Nẵng - Hội An như Mai Linh, Vinasun, Ti&ecirc;n Sa,... hoặc bạn cũng c&oacute; thể sử dụng dịch vụ đưa đ&oacute;n s&acirc;n bay c&oacute; t&iacute;nh ph&iacute; của resort. &nbsp;</p>\r\n\r\n<p>Từ s&acirc;n bay Đ&agrave; Nẵng về resort khoảng 43 km tương đương 1 giờ đi xe. Gi&aacute; taxi một chiều khoảng 480.000 - 600.000 vnđ t&ugrave;y loại xe 4 hay 7 chỗ, t&ugrave;y h&atilde;ng xe bạn chọn v&agrave; thời điểm đặt xe.</p>\r\n\r\n<p>Từ s&acirc;n bay Chu Lai về resort khoảng 64,5 km tương đương 1tiếng15p đi xe. Gi&aacute; taxi một chiều khoảng 660.000 - 790.000 vnđ t&ugrave;y loại xe 4 hay 7 chỗ, t&ugrave;y h&atilde;ng xe bạn chọn v&agrave; thời điểm đặt xe.</p>\r\n\r\n<p>Resort cung cấp dịch vụ đưa đ&oacute;n s&acirc;n bay c&oacute; t&iacute;nh ph&iacute;. Mức gi&aacute; c&oacute; thể thay đổi t&ugrave;y thời điểm đặt xe. Qu&yacute; kh&aacute;ch c&oacute; nhu cầu vui l&ograve;ng li&ecirc;n hệ lễ t&acirc;n trước để được kiểm tra t&igrave;nh trạng c&ocirc;ng suất v&agrave; sắp xếp đưa đ&oacute;n ph&ugrave; hợp.</p>\r\n\r\n<p>Di chuyển bằng xe kh&aacute;ch: Từ H&agrave; Nội, hoặc TPHCM,&hellip; c&aacute;c bạn c&oacute; thể di chuyển bằng xe kh&aacute;ch đến Đ&agrave; Nẵng với gi&aacute; khoảng 320.000vnđ/người/chuyến. Sau đ&oacute; di chuyển bằng taxi đến Vinpearl Resort &amp; Golf Nam Hội An.<br />\r\nTIỆN &Iacute;CH V&Agrave; GIẢI TR&Iacute;</p>\r\n\r\n<p>Nh&agrave; h&agrave;ng v&agrave; bar: đa số phục vụ ẩm thực Hội An truyền thống. C&aacute;c nh&agrave; h&agrave;ng v&agrave; bar ở đ&acirc;y sẽ gi&uacute;p bạn cảm nhận một c&aacute;ch r&otilde; n&eacute;t nhất những tinh hoa, những n&eacute;t đặc sắc nhất trong ẩm thực Hội An. Ngo&agrave;i ra c&aacute;c nh&agrave; h&agrave;ng cũng phục vụ đa dạng c&aacute;c m&oacute;n từ &Aacute; sang &Acirc;u mang đến nhiều cảm nhận phong ph&uacute; hơn trong ẩm thực.<br />\r\nTrung t&acirc;m hội nghị: Vinpearl Nam Hội An Resort &amp; Golf sở hữu kh&ocirc;ng gian hội nghị lớn v&agrave; nhiều ph&ograve;ng nhỏ được trang bị đầy đủ tiện nghi, hiện đại, th&iacute;ch hợp cho mọi nhu cầu tổ chức sự kiện v&agrave; hội họp.<br />\r\nGiải tr&iacute;: Cung cấp đầy đủ c&aacute;c dịch vụ spa, thể h&igrave;nh, c&acirc;u lạc bộ cho trẻ em,&hellip; nhằm đ&aacute;p ứng đủ c&aacute;c nhu cầu, mag đến cho bạn một kỳ nghỉ trọn vẹn.<br />\r\n+ Trong đ&oacute;, nổi bậc hơn cả l&agrave; Vinpearl River Safari C&ocirc;ng vi&ecirc;n động vật phi&ecirc;n bản du khảo bằng đường thủy đầu ti&ecirc;n v&agrave; duy nhất tại Việt Nam, nơi du kh&aacute;ch sẽ cảm nhận như đang ngồi giữa d&ograve;ng Amazon huyền thoại để kh&aacute;m ph&aacute; cuộc sống hoang d&atilde; của c&aacute;c lo&agrave;i động vật qu&yacute; hiếm hai b&ecirc;n bờ như hổ Bengal, linh dương, kangaroo, t&ecirc; gi&aacute;c, hươu cao cổ, ngựa vằn&hellip;</p>\r\n\r\n<p>+ C&ograve;n c&oacute; khu Vinpearl Golf Nam Hội An, giờ mở cửa: 06:00 &ndash; 19:00. S&acirc;n golf 18 hố b&ecirc;n bờ biển B&igrave;nh Minh, với c&aacute;c điểm ph&aacute;t b&oacute;ng được thiết kế ở tầm cao đảm bảo tầm nh&igrave;n xa &ldquo;ho&agrave;n hảo&rdquo;, địa h&igrave;nh tuyệt mỹ c&ugrave;ng cảnh đẹp say đắm l&ograve;ng người, mang đến những gi&acirc;y ph&uacute;t kh&oacute; qu&ecirc;n cho bất cứ ai từng đặt ch&acirc;n tới đ&acirc;y.</p>\r\n\r\n<p>+ VinEco Nam Hội An l&agrave; khu sinh th&aacute;i xanh m&aacute;t diện t&iacute;ch 12,4ha, nơi du kh&aacute;ch thỏa sức trải nghiệm quy tr&igrave;nh trồng rau củ quả kh&eacute;p k&iacute;n v&agrave; cảm nhận sự tươi ngon của n&ocirc;ng sản sạch ngay tại vườn. C&aacute;c khu chức năng bao gồm: 1 nh&agrave; k&iacute;nh Marchagey 1.000m2 nhập khẩu từ Ph&aacute;p, 3 nh&agrave; k&iacute;nh TAP nhập khẩu từ Israel, 15ha trồng c&acirc;y ăn quả, đồng ruộng, cảnh quan v&agrave; hạ tầng phụ trợ. Mở cửa từ 6:00 &ndash; 19:00.</p>\r\n\r\n<p>+ Khu Vinpear Land Nam Hội An Mở cửa cả ng&agrave;y. Tổ hợp khu vui chơi giải tr&iacute; với diện t&iacute;ch trải rộng 62,4 ha, bao gồm v&ugrave;ng đất phi&ecirc;u lưu v&agrave; c&ocirc;ng vi&ecirc;n nước &ndash; thi&ecirc;n đường giải tr&iacute; thời thượng, Đảo D&acirc;n gian với sự hội tụ của tinh hoa truyền thống Việt, v&agrave; Bến cảng Giao thoa, nơi du kh&aacute;ch c&oacute; thể thưởng ngoạn kh&ocirc;ng gian văn ho&aacute; t&aacute;i hiện phố cổ Hội An c&ugrave;ng những quần thể kiến tr&uacute;c đậm chất ch&acirc;u &Acirc;u.</p>', 1, 3, 3, NULL, '2021-07-15 17:14:27', '2023-05-02 09:58:04'),
(5, 'Combo Du lịch Đà Nẵng Khách sạn 5 Sao Radisson Đà Nẵng từ Sài Gòn 2023', 'https://dulichviet.com.vn/images/bandidau/NOI-DIA/Da-Nang/Radisson-da-nang-du-lich-viet-23.jpg', 'Sài gòn', '02136548962', NULL, 1999000, 0, 0, 'Tọa lạc trên một ngọn núi và bao quanh bởi biển, Romana Resort & Spa Phan Thiết mang đến cho khách hàng khung cảnh tuyệt đẹp của vịnh và núi. Khu nghỉ mát có 4 phòng Junior Suites, 28 biệt thự có bể bơi riêng và 64 phòng Deluxe. Tất cả các phòng đều nhìn ra đại dương và được thiết kế riêng tư sang trọng. Các Nhà hàng và Bar tại Romana Resort & Spa là một trong những nhà hàng và bar tốt nhất ở Mũi Né, Phan Thiết. Nhà hàng Panorama sẽ mang đến cho quý khách sự trải nghiệm những khoảnh khắc đáng nhớ khi vừa thưởng thức bữa tối tuyệt vời vừa ngắm nhìn cảnh hoàng hôn tuyệt đẹp. Nhà hàng phục vụ các thực đơn đa dạng, từ các món ăn khai vị tinh tế, salad nhiệt đới đến các loại hải sản tươi sống.', '<p>p dụng từ: &nbsp;10/10 đến 20/12/2022</p>', 1, 3, 3, NULL, '2023-05-03 13:32:57', '2023-05-03 22:59:53'),
(6, 'VINPEARL RESORT & SPA PHU QUOC', 'https://dulichviet.com.vn/images/bandidau/DU%20L%E1%BB%8ACH%20T%E1%BB%B0%20CH%E1%BB%8CN%202019/Hotel/khach-san-phu-quoc-spa-du-lich-viet.jpg', 'Phú Quốc', '0902.976.588', NULL, 1499000, 0, 0, 'VỊ TRÍ\n\nVinpearl Resort & Spa  Phú Quốc tọa lạc tại Gành Dầu, Phú Quốc, sở hữu vị trí lưu trú vô cùng thuận lợi, du khách có thể dễ dàng ghé thăm những địa điểm du lịch nổi tiếng của Phú Quốc trong chuyến hành trình của mình.\n\nVinpearl Resort & Spa Phu Quốc là lựa chọn sáng giá dành cho những ai đang tìm kiếm một trải nghiệm xa hoa đầy thú vị trong kỳ nghỉ của mình. Lưu trú tại đây cũng là cách để quý khách chiều chuộng bản thân với những dịch vụ xuất sắc nhất và khiến kỳ nghỉ của mình trở nên thật đáng nhớ.\n\nResort đi vào hoạt động tháng 11/2014 do Tập đoàn Vingroup xây dựng và quản lý. Resort gồm 750 phòng ở khu tòa nhà và khu biệt thự 3 - 4 phòng ngủ, mang đậm kiến trúc Á Đông, với đặc trưng mái ngói đỏ mang thích cảm giác thanh bình, thư thái cho du khách. Nổi bậc là thiết kế các chòi Spa trên mặt hồ vô cùng độc đáo đem lại cảm giác thư giãn tuyệt đối, tận hưởng một cuộc sống êm đềm, nhẹ nhàng, bỏ lại bao xô bồ bên ngoài của cuộc sống.\n\nVinpearl Resort & Spa  Phú Quốc  đã xuất xắc lọt vào top 15 “Khách sạn 5 sao hàng đầu Việt Nam” trong hạng mục Giải thưởng Du lịch Việt Nam 2018 do Tổng Cục Du lịch Việt Nam tổ chức.\n\nƯu điểm nổi bật của hệ thống Vinpearl Phú Quốc:\n\n- Miễn phí xe đón tiễn sân bay (khoảng 32,7 km) & bến tàu Bãi Vòng (khoảng 40 km) theo lịch trình chuyến bay của khách.\n- Miễn phí xe đến trung tâm Dương Đông (khoảng 22 km).\n- Miễn phí xe điện di chuyển giữa Vinpearl Land & Vinpearl Safari. Vui chơi không giới hạn tại Vinpearl Land hoặc Safari theo gói nghỉ dưỡng.\n- Miễn phí sử dụng minibar trong phòng.\nĐể sử dụng các dịch vụ di chuyển miễn phí của hệ thông Vinpearl Phú Quốc, bạn vui lòng liên hệ cho lễ tân khách sạn để chuẩn bị trước nhé! Quầy tiếp tân 24 giờ luôn sẵn sàng phục vụ quý khách từ thủ tục nhận phòng đến trả phòng hay bất kỳ yêu cầu nào.\nNHÀ HÀNG VÀ SPA\n\nNhà hàng thiết kế thanh lịch và tinh tế đến từng chi tiết, hướng ra biển hoặc hướng về hồ bơi,… bạn có thể vừa nhâm nhi, thưởng thức các món ăn, thức uống vừa ngon lành vừa đẹp mắt đặt để trên bàn mà vừa có thể ngắm nhìn thiên nhiên dần thay đổi theo dòng thời gian ngoài kia.\n\nCác nhà hàng và Spa đặt ở vị trí vô cùng thuận tiện cho du khách, đa số được đặt gần hồ bơi, hoặc tầng 1 của tòa Ocean và Coral. Thời gian mở cửa đa số từ 7:00 – 23:00, riêng nhà hang Pepper chỉ mở cửa trong thời gian 18:00 – 22:00.\n\nTRUNG TÂM HỘI NGHỊ\n\nHệ thống phòng họp và hội nghị được thiết kế hiện đại, sang trọng với tổng diện tích 2.800m2. Có đầy đủ các dịch vụ và tiện nghi đáp ứng mọi nhu cầu của du khách.\n\nGIẢI TRÍ\n\nGần Vinpearl Golf, Vinpearl Land, Vinpearl Safari, có đầy đủ hệ thông gym, hồ bơi, cau lạc bộ dành cho trẻ em, đặc biệt là các dịch vụ Spa ở đây với các liệu pháp spa tinh tuý nhất giúp thư giãn tinh thần và làm tươi trẻ cơ thể.', '<p>ad</p>', 1, 3, 4, NULL, '2023-05-14 14:01:07', '2023-05-14 14:01:07'),
(7, 'VINPEARL RESORT & GOLF PHU QUOC', 'https://dulichviet.com.vn/images/bandidau/DU%20L%E1%BB%8ACH%20T%E1%BB%B0%20CH%E1%BB%8CN%202019/Hotel/du-lich-viet(4).jpg', 'Phú Quốc', '0932.659.588', NULL, 299000, 0, 0, 'VỊ TRÍ\n\nTọa lạc tại Gành Dầu, Vinpearl Resort & Golf Phú Quốc là địa điểm hoàn hảo để khám phá Đảo Phú Quốc và các khu vực lân cận. Chỉ cách khu vui chơi Vinpearl Land Phú Quốc 1,5km, Vinpearl Safari khoảng 5,6km và cách thị trấn Dương Đông khoảng 22,6km,… đây là vị trí rất thuận tiện cho du khách có thể thoải mái tham quan, khám phá các điểm du lịch nổi tiếng ở hòn đảo Phú Quốc xinh đẹp.\n\nResort chính thức khai trương vào 11/2015 do Tập đoàn Vingroup quản lý và đầu tư với hệ thống 381 phòng khách sạn và 182 căn biệt thự. Nhiều phòng còn có các tiện nghi như trà miễn phí, khăn, thảm, giá treo quần áo, cân. Vinpearl Resort & Golf Phú Quốc cung cấp nhiều hoạt động vui chơi giải trí độc đáo như công viên giải trí, công viên nước, lặn biển (ống thở), bãi biển riêng, đặc biệt là các sân golf mini,….\n\nVinpearl Resort & Golf Phú Quốc là nơi tuyệt vời, phù hợp với các chuyến du lịch nghỉ dưỡng. Bởi Không gian vừa sang trọng, cổ kín bên cạnh là sân gold 18 hố được đặt bên cạnh cảnh quan thiên nhiên hoang sơ hùng vĩ của Phú Quốc. Còn gì tuyệt vời hơn khi vừa được thư giãn vừa được thưởng thức sự hòa hợp độc đáo như vậy đúng không nào?\n\nPHƯƠNG TIỆN DI CHUYỂN\n\nCác chuyến bay đến Phú Quốc:\n\nHà Nội - Phú Quốc: Thời gian di chuyển khoảng 02 giờ.\n\nHải Phòng - Phú Quốc: Thời gian di chuyển khoảng 02 giờ.\n\nSài Gòn - Phú Quốc: Thời gian di chuyển khoảng 01 giờ.\n\nCần Thơ - Phú Quốc: Thời gian di chuyển khoảng 55 phút. \n\nTừ sân bay, các bạn có thể đi taxi đến các khu resort, giá dao động khoảng từ 390,000 – 490,000 vnđ. Hoặc có thể chọn dịch vụ đón tiễn sân bay của Vinpearl Resort & Golf, nếu bạn lựa chọn dịch vụ tại đây, bạn vui lòng liên hệ trước lễ tân của Vinpearl Resort & Golf để sắp xếp lịch trình.\n\nNHÀ HÀNG VÀ BAR\n\nHệ thống các quán bar và nhà hàng như Pool Bar, Patio Lounge, The Prime Bar & Grill, Beach House chuẩn 5 sao, với thiết kế sang trọng, cổ điển. Phục vụ tận tâm, món ăn tươi ngon, tinh tế và chuẩn vị.   \nTrung tâm hội nghị: với thiết kế mang đậm phong cách Châu Âu, Vinpearl Resort & Golf Phú Quốc là điểm đến du lịch hàng đầu cho du khách có nhu cầu nghĩ dưỡng kết hợp với hội họp và sự kiện.\nVinpearl Golf: Được thiết kế bởi IMG Worldwide, Vinpearl Golf Phú Quốc là sân golf 18 hố chuẩn quốc tế đầu tiên trên đảo Phú Quốc với nhiều thử thách bất ngờ được tạo ra. Sân gôn được đầu tư xây dựng trên diện tích 80 ha theo mô hình sân gôn biển đảo độc đáo, với 2 cụm sân được bố trí tại 2 vị trí hoàn toàn khác nhau. Mỗi sân mang dấu ấn riêng từ địa hình khu vực đến các chướng ngại vật trên sân với những mức độ khó khác nhau, mang đến trải nghiệm đa dạng cho người chơi.\nAkoya Spa: là chuỗi các spa được được vận hành bởi nhà quản lý spa nổi tiếng thế giới Mandara. Với các phương pháp trị liệu nổi tiếng của bali, Hawai,… đem đến cho bạn cảm giác thư giản tuyệt dối.\nHồ bơi ngoài trời: nằm sát biển, mở cửa từ 6:00 – 19:00, vừa an toàn, mà lại có cảm giác giống như đang hòa mình giữa tự nhiên rộng lớn.\nKhu vui chơi trẻ em: vị trí tầng trệt, cánh A của tòa khách sạn. Có dịch vụ giữ trẻ với các trò chơi thú vị, sáng tạo giúp cha mẹ có thể yên tâm thưởng thức, tự do tận hưởng chuyến du lịch của mình.\nTrung tâm thể hình: vị trí tầng 2, cánh C – tòa khách sạn, mở cửa từ 6:00 – 21:00. Được trang bị đầy đủ các thiết bị, tiện nghi, hiện đại. Bạn có thể yên tâm vui chơi và tập luyện giữ cho mình một sức khỏe tốt.\nHoạt động thể thao: Kỳ nghỉ tại Vinpearl resort & spa Phú Quốc đem đến cho du khách vô vàn trải nghiệm giải trí thú vị với các môn thể thao và hoạt động dưới nước như sân quần vợt, lớp học Taichi, Yoga, Aerobic hồ bơi, nhảy dù, lướt sóng , lặn biển, câu cá trên biển, chèo thuyền kayak, phao chuối, dù lượn…', '<p>&aacute;da</p>', 1, 3, 4, NULL, '2023-05-14 14:03:36', '2023-05-14 14:03:36'),
(8, 'V Hotel Lavender agoda', 'https://dulichviet.com.vn/images/bandidau/images/KH%C3%81CH%20S%E1%BA%A0N/phong-grand-dune-khach-san-the-grand-ho-tram-strip-resort_du-lich-viet.jpg', 'Hà Nội', '0902.976.588', NULL, 1999000, 0, 0, 'VỊ TRÍ\n\nVinpearl Discovery 2 tọa lạc tại Bãi Dài, gành Dầu Phú Quốc, thuộc hệ thống Vinpearl Resort & Villas – hệ thống nghĩ dưỡng đẳng cấp 5 sao. Là quần thể villa gồm 332 căn biệt thự nhiều phòng ngủ.\n\nCác ngôi biệt thự tại đây được thiết kế theo phong cách Địa Trung Hải là điểm nhấn khác biệt so với các resort khác, được trang bị đầy đủ tiện nghi cao cấp phục vụ nhu cầu sử dụng của mọi người. Các dịch vụ cũng rất đa dạng như ăn uống, thể thao, mua sắm,... Trong phòng còn được trang bị tivi kết nối truyền hình cáp, mạng wifi, tủ lạnh rất tiện nghi.\n\nPHƯƠNG TIỆN DI CHUYỂN\n\nCác chuyến bay đến Phú Quốc:\n- Hà Nội - Phú Quốc: Thời gian di chuyển khoảng 02 giờ.\n\n- Hải Phòng - Phú Quốc: Thời gian di chuyển khoảng 02 giờ.\n\n- Sài Gòn - Phú Quốc: Thời gian di chuyển khoảng 01 giờ.\n\n- Cần Thơ - Phú Quốc: Thời gian di chuyển khoảng 55 phút. \n\nTừ sân bay, các bạn có thể đi taxi đến các khu resort, giá dao động khoảng từ 400,000 – hơn 500,000 vnđ. Hoặc có thể chọn dịch vụ đón tiễn sân bay của Vinpearl Discovery 2, nếu bạn lựa chọn dịch vụ tại đây, bạn vui lòng liên hệ trước lễ tân của Vinpearl Discovery 2 để sắp xếp lịch trình.\nNHÀ HÀNG VÀ BAR\n\nĐến với các nhà hang của Vinpearl Discovery 2 Phú Quốc bạn sẽ không hề thất vọng, bởi chất lượng món ăn cũng như thái độ phục vụ vô cùng tận tâm chu đáo. Gồm 2 chuỗi nhà hàng:  Ocenus phục vụ đa dạng các món, quy tụ đa dạng tinh hoa ẩm thực trong nước và quốc tế. Còn đến với nhà hang Sabbia, bạn vừa có thyể thưởng thức món ăn ngon đẳng cấp mà vừa có thể tận hưởng không khí biển, mát lành, thiên nhiên kỳ vỹ. Đặc biệt bạn có thể thưởng thức những hương vị tươi ngon, tinh túy nhất của các loài hải sản, đậm chất ẩm thực Việt Nam.\n\nGIẢI TRÍ\n\nCác dịch vụ giải trí vô cùng đa dạng, giúp bạn thư giãn tuyệt đối và mang đến nhiều trải nghiệm vô cùng tuyệt vời khi lựa chọn Vinpearl Discovery 2 Phú Quốc là điểm đến.', '<p>&aacute;d</p>', 1, 3, 1, NULL, '2023-05-14 14:04:52', '2023-05-14 14:04:52'),
(9, 'Đặt phòng khách sạn Victoria Phan Thiet Beach Resort 4 sao', 'https://dulichviet.com.vn/images/bandidau/images/KH%C3%81CH%20S%E1%BA%A0N/TRONG%20N%C6%AF%E1%BB%9AC/MI%E1%BB%80N%20NAM/PHAN%20THIET/VICTORIA%20PHAN%20THIET%20BEACH%20RESORT/dat-phong-khach-san-victoria-phan-thiet-beach-resort-2017_du-lich-viet.png', 'Phan Thiết', '0932.659.588', NULL, 999000, 0, 0, 'VỊ TRÍ\n\nVinpearl Discovery 2 tọa lạc tại Bãi Dài, gành Dầu Phú Quốc, thuộc hệ thống Vinpearl Resort & Villas – hệ thống nghĩ dưỡng đẳng cấp 5 sao. Là quần thể villa gồm 332 căn biệt thự nhiều phòng ngủ.\n\nCác ngôi biệt thự tại đây được thiết kế theo phong cách Địa Trung Hải là điểm nhấn khác biệt so với các resort khác, được trang bị đầy đủ tiện nghi cao cấp phục vụ nhu cầu sử dụng của mọi người. Các dịch vụ cũng rất đa dạng như ăn uống, thể thao, mua sắm,... Trong phòng còn được trang bị tivi kết nối truyền hình cáp, mạng wifi, tủ lạnh rất tiện nghi.\n\nPHƯƠNG TIỆN DI CHUYỂN\n\nCác chuyến bay đến Phú Quốc:\n- Hà Nội - Phú Quốc: Thời gian di chuyển khoảng 02 giờ.\n\n- Hải Phòng - Phú Quốc: Thời gian di chuyển khoảng 02 giờ.\n\n- Sài Gòn - Phú Quốc: Thời gian di chuyển khoảng 01 giờ.\n\n- Cần Thơ - Phú Quốc: Thời gian di chuyển khoảng 55 phút. \n\nTừ sân bay, các bạn có thể đi taxi đến các khu resort, giá dao động khoảng từ 400,000 – hơn 500,000 vnđ. Hoặc có thể chọn dịch vụ đón tiễn sân bay của Vinpearl Discovery 2, nếu bạn lựa chọn dịch vụ tại đây, bạn vui lòng liên hệ trước lễ tân của Vinpearl Discovery 2 để sắp xếp lịch trình.\nNHÀ HÀNG VÀ BAR\n\nĐến với các nhà hang của Vinpearl Discovery 2 Phú Quốc bạn sẽ không hề thất vọng, bởi chất lượng món ăn cũng như thái độ phục vụ vô cùng tận tâm chu đáo. Gồm 2 chuỗi nhà hàng:  Ocenus phục vụ đa dạng các món, quy tụ đa dạng tinh hoa ẩm thực trong nước và quốc tế. Còn đến với nhà hang Sabbia, bạn vừa có thyể thưởng thức món ăn ngon đẳng cấp mà vừa có thể tận hưởng không khí biển, mát lành, thiên nhiên kỳ vỹ. Đặc biệt bạn có thể thưởng thức những hương vị tươi ngon, tinh túy nhất của các loài hải sản, đậm chất ẩm thực Việt Nam.\n\nGIẢI TRÍ\n\nCác dịch vụ giải trí vô cùng đa dạng, giúp bạn thư giãn tuyệt đối và mang đến nhiều trải nghiệm vô cùng tuyệt vời khi lựa chọn Vinpearl Discovery 2 Phú Quốc là điểm đến.', '<p>&aacute;dd</p>', 1, 3, 3, NULL, '2023-05-14 14:05:54', '2023-05-14 14:05:54'),
(10, 'Đặt phòng khách sạn Grand Hồ Tràm 5 sao', 'https://dulichviet.com.vn/images/bandidau/images/KH%C3%81CH%20S%E1%BA%A0N/dat-phong-khach-san-the-grand-ho-tram-gia-re_du-lich-viet.png', 'Hồ Chí Minh', '0932.659.588', NULL, 1499000, 0, 0, 'VỊ TRÍ\n\nVinpearl Discovery 2 tọa lạc tại Bãi Dài, gành Dầu Phú Quốc, thuộc hệ thống Vinpearl Resort & Villas – hệ thống nghĩ dưỡng đẳng cấp 5 sao. Là quần thể villa gồm 332 căn biệt thự nhiều phòng ngủ.\n\nCác ngôi biệt thự tại đây được thiết kế theo phong cách Địa Trung Hải là điểm nhấn khác biệt so với các resort khác, được trang bị đầy đủ tiện nghi cao cấp phục vụ nhu cầu sử dụng của mọi người. Các dịch vụ cũng rất đa dạng như ăn uống, thể thao, mua sắm,... Trong phòng còn được trang bị tivi kết nối truyền hình cáp, mạng wifi, tủ lạnh rất tiện nghi.\n\nPHƯƠNG TIỆN DI CHUYỂN\n\nCác chuyến bay đến Phú Quốc:\n- Hà Nội - Phú Quốc: Thời gian di chuyển khoảng 02 giờ.\n\n- Hải Phòng - Phú Quốc: Thời gian di chuyển khoảng 02 giờ.\n\n- Sài Gòn - Phú Quốc: Thời gian di chuyển khoảng 01 giờ.\n\n- Cần Thơ - Phú Quốc: Thời gian di chuyển khoảng 55 phút. \n\nTừ sân bay, các bạn có thể đi taxi đến các khu resort, giá dao động khoảng từ 400,000 – hơn 500,000 vnđ. Hoặc có thể chọn dịch vụ đón tiễn sân bay của Vinpearl Discovery 2, nếu bạn lựa chọn dịch vụ tại đây, bạn vui lòng liên hệ trước lễ tân của Vinpearl Discovery 2 để sắp xếp lịch trình.\nNHÀ HÀNG VÀ BAR\n\nĐến với các nhà hang của Vinpearl Discovery 2 Phú Quốc bạn sẽ không hề thất vọng, bởi chất lượng món ăn cũng như thái độ phục vụ vô cùng tận tâm chu đáo. Gồm 2 chuỗi nhà hàng:  Ocenus phục vụ đa dạng các món, quy tụ đa dạng tinh hoa ẩm thực trong nước và quốc tế. Còn đến với nhà hang Sabbia, bạn vừa có thyể thưởng thức món ăn ngon đẳng cấp mà vừa có thể tận hưởng không khí biển, mát lành, thiên nhiên kỳ vỹ. Đặc biệt bạn có thể thưởng thức những hương vị tươi ngon, tinh túy nhất của các loài hải sản, đậm chất ẩm thực Việt Nam.\n\nGIẢI TRÍ\n\nCác dịch vụ giải trí vô cùng đa dạng, giúp