INSERT INTO `tours` (`id`, `t_title`, `t_journeys`, `t_schedule`, `t_move_method`, `t_starting_gate`, `t_start_date`, `t_end_date`, `t_number_guests`, `t_price_adults`, `t_price_children`, `t_sale`, `t_view`, `t_description`, `t_content`, `t_anbum_image`, `t_image`, `t_location_id`, `t_user_id`, `t_number_registered`, `t_follow`, `t_status`, `t_service_id`, `created_at`, `updated_at`) VALUES
(1, 'Du lịch Hà Nội - Yên Tử - Hạ Long - Tràng An - Sapa - Fansipan từ Sài Gòn', 'Hà Nội - Yên Tử - Hạ Long - Tràng An - Sapa - Fansipan', '6 ngày 5 đêm', NULL, NULL, '2023-04-12', '2023-04-18', 20, 600000, 560000, 20, 0, 'Quay ngược thời gian về năm 968 và cho đến năm 1009 (dưới triều Đình), Hoa Lư đã từng là thủ đô của Việt Nam. Và mặc dù danh xưng này đã thuộc về Thăng Long vào năm 1010, nơi này vẫn còn lưu giữ cả kho báu lịch sử cho đến ngày nay. Khu du lịch Tam Cốc Bích Động còn được biết đến với tên gọi là “vịnh Hạ Long trên cạn” với vẻ đẹp yên bình của những cánh đồng lúa và các dòng sông uốn lượn quanh các ngọn núi. Những hang động đá vôi độc đáo cùng các điểm du lịch tâm linh như đền vua Đinh và đền Vua Lê chắc chắn sẽ là những địa điểm bạn không nên bỏ qua cho chuyến du lịch đầu xuân.', 'Quay ngược thời gian về năm 968 và cho đến năm 1009 (dưới triều Đình), Hoa Lư đã từng là thủ đô của Việt Nam. Và mặc dù danh xưng này đã thuộc về Thăng Long vào năm 1010, nơi này vẫn còn lưu giữ cả kho báu lịch sử cho đến ngày nay. Khu du lịch Tam Cốc Bích Động còn được biết đến với tên gọi là “vịnh Hạ Long trên cạn” với vẻ đẹp yên bình của những cánh đồng lúa và các dòng sông uốn lượn quanh các ngọn núi. Những hang động đá vôi độc đáo cùng các điểm du lịch tâm linh như đền vua Đinh và đền Vua Lê chắc chắn sẽ là những địa điểm bạn không nên bỏ qua cho chuyến du lịch đầu xuân.', NULL, 'https://dulichviet.com.vn/images/bandidau/NOI-DIA/Quy-Nhon/ghenh-da-dia-du-lich-quy-nhon-du-lich-viet.jpg', 1, 1, 21, 5, 1, 2, '2021-07-03 19:20:09', '2023-05-03 22:50:20'),
